import React from "react";
import { CChartLine } from "@coreui/react-chartjs";
import { getStyle, hexToRgba } from "@coreui/utils/src";
import { number } from "prop-types";

const brandInfo = getStyle("info") || "#20a8d8";
const initialState = { xAxis: [], yAxis: [] };
const MainChart = ({ chartData }) => {
  const [{ xAxis, yAxis }, setState] = React.useState(initialState);
  React.useEffect(() => {
    setState(
      chartData.reduce(
        (acc, [first, second]) => {
          acc.xAxis.push(first);
          acc.yAxis.push(second);
          return acc;
        },
        { xAxis: [], yAxis: [] }
      )
    );
  }, []);

  // const pz = [];

  // if (arr && arr[0] && arr[0].data) {
  //   arr[0].data.forEach(([f, s]) => pz.push(f, s))
  // }

  // console.log("pzzzzz =>", pz);
  // const arr5 = [];
  // const arr6 = [];
  // for (let x = 0; x < pz.length; x += 2) {
  //   arr5.push(pz[x]);
  // }
  // for (let x = 1; x < pz.length; x += 2) {
  //   arr6.push(pz[x]);
  // }
  // console.log("arr5=>", arr5);
  // console.log("arr6=>", arr6);
  const defaultDatasets = (() => {
    let elements = 27;
    const data = [];
    for (let i = 0; i <= elements; i++) {
      data.push(...yAxis);
    }

    return [
      {
        label: "My First dataset",
        backgroundColor: hexToRgba(brandInfo, 10),
        borderColor: brandInfo,
        pointHoverBackgroundColor: brandInfo,
        borderWidth: 2,
        data: data,
      },
    ];
  })();

  const defaultOptions = (() => {
    return {
      maintainAspectRatio: false,
      legend: {
        display: false,
      },
      scales: {
        xAxes: [
          {
            gridLines: {
              drawOnChartArea: false,
            },
          },
        ],
        yAxes: [
          {
            ticks: {
              beginAtZero: true,
              maxTicksLimit: 5,
              stepSize: Math.ceil(250 / 5),
              max: 2000,
            },
            gridLines: {
              display: true,
            },
          },
        ],
      },
      elements: {
        point: {
          radius: 0,
          hitRadius: 10,
          hoverRadius: 4,
          hoverBorderWidth: 3,
        },
      },
    };
  })();
  const azz = [
    "Mo",
    "Tu",
    "We",
    "Th",
    "Fr",
    "Sa",
    "Su",
    "Mo",
    "Tu",
    "We",
    "Th",
    "Fr",
    "Sa",
    "Su",
    "Mo",
    "Tu",
    "We",
    "Th",
    "Fr",
    "Sa",
    "Su",
    "Mo",
    "Tu",
    "We",
    "Th",
    "Fr",
    "Sa",
    "Su",
  ];
  // console.log("refer", arr6);
  // render

  return (
    <CChartLine
      style={{ height: "300px", marginTop: "40px" }}
      datasets={defaultDatasets}
      options={defaultOptions}
      labels={xAxis}
    />
  );
};

export default MainChart;
