import React from "react";
import { CChartLine } from "@coreui/react-chartjs";
import { getStyle, hexToRgba } from "@coreui/utils/src";
import { number } from "prop-types";

const brandInfo = getStyle("info") || "#20a8d8";

const MainChart = (attributes, arr) => {
  const random = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1) + min);
  };

  // console.log(arr[0].data);
  const pz = [];
  // for (let p = 0; p <= arr[0].data.length; p++) {
  //   console.log("checking =>", arr[0].data[p]);
  // }
  // console.log("arr", arr[0].data[0][1]);
  // console.log("length", arr[0].data.length);
  // arr.map((item, index) => console.log(pz.push(item.data[index][1])));
  // console.log(pz);
  for (let i = 0; i < arr[0].data.length; i++) {
    // get the size of the inner array
    var innerArrayLength = arr[0].data[i].length;
    // loop the inner array
    for (let j = 0; j < innerArrayLength; j++) {
      // console.log(arr[0].data[i][j]);
      pz.push(arr[0].data[i][j]);
    }
  }
  console.log(pz);
  const arr5 = [];
  const arr6 = [];
  for (let x = 0; x < pz.length; x += 2) {
    arr5.push(pz[x]);
  }
  for (let x = 1; x < pz.length; x += 2) {
    arr6.push(pz[x]);
  }
  console.log("arr5=>", arr5);
  console.log("arr6=>", arr6);
  const defaultDatasets = (() => {
    let elements = 27;
    const data = [];
    for (let i = 0; i <= elements; i++) {
      data.push(...arr6);
    }
    // console.log(data);
    return [
      {
        label: "My First dataset",
        backgroundColor: hexToRgba(brandInfo, 10),
        borderColor: brandInfo,
        pointHoverBackgroundColor: brandInfo,
        borderWidth: 2,
        data: data,
      },
    ];
  })();

  const defaultOptions = (() => {
    return {
      maintainAspectRatio: false,
      legend: {
        display: false,
      },
      scales: {
        xAxes: [
          {
            gridLines: {
              drawOnChartArea: false,
            },
          },
        ],
        yAxes: [
          {
            ticks: {
              beginAtZero: true,
              maxTicksLimit: 5,
              stepSize: Math.ceil(250 / 5),
              max: 2000,
            },
            gridLines: {
              display: true,
            },
          },
        ],
      },
      elements: {
        point: {
          radius: 0,
          hitRadius: 10,
          hoverRadius: 4,
          hoverBorderWidth: 3,
        },
      },
    };
  })();
  const azz = [
    "Mo",
    "Tu",
    "We",
    "Th",
    "Fr",
    "Sa",
    "Su",
    "Mo",
    "Tu",
    "We",
    "Th",
    "Fr",
    "Sa",
    "Su",
    "Mo",
    "Tu",
    "We",
    "Th",
    "Fr",
    "Sa",
    "Su",
    "Mo",
    "Tu",
    "We",
    "Th",
    "Fr",
    "Sa",
    "Su",
  ];
  console.log("refer", arr6);
  // render
  return (
    <CChartLine
      {...attributes}
      datasets={defaultDatasets}
      options={defaultOptions}
      labels={arr5}
    />
  );
};

export default MainChart;
