import React from "react";
import {
  CButtonGroup,
  CCard,
  CCardBody,
  CCardFooter,
  CCol,
  CProgress,
  CRow,
} from "@coreui/react";
//Chart
//Components
import DatePicker from "../components/DatePicker";
import Chart from "../components/Chart";

const Dashboard = () => {
  const submitFunction = (startDate, endDate) => {
    const startArr = moment(startDate).format("YYYY/M/DD").split("/");
    const endArr = moment(endDate).format("YYYY/M/DD").split("/");

    const requestBody = {
      searchDateEnd: {
        day: endArr[2],
        month: endArr[1],
        year: endArr[0],
      },
      searchDateStart: {
        day: startArr[2],
        month: startArr[1],
        year: startArr[0],
      },
      deviceType: "WELL",
      measurementType: "NORMAL",
    };

    fetch("http://mesurit.com:8086/api/data/search/", {
      method: "POST",
      mode: "cors",
      credentials: "same-origin",
      headers: {
        Authorization: `Basic ${btoa(`Guest:WaterFlow`)}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify(requestBody),
    })
      .then((res) => res.json())
      .then((res) => console.log(res));
  };

  function submitFilter({ startDate, endDate }) {
    if (endDate !== date.endDate) {
      submitFunction(startDate, endDate);
    }

    setDate({ startDate, endDate });
  }
  return (
    <>
      <CCard>
        <CCardBody>
          <CRow>
            <CCol sm="5">
              <h4 id="traffic" className="card-title mb-0">
                Traffic
              </h4>
              <div className="small text-muted">November 2017</div>
            </CCol>
            <CCol sm="7" className="d-none d-md-block">
              <CButtonGroup className="float-right mr-3">
                <DatePicker submitFilter={submitFilter} />
              </CButtonGroup>
            </CCol>
          </CRow>
          <Chart style={{ height: "300px", marginTop: "40px" }} />
        </CCardBody>
        <CCardFooter>
          <CRow className="text-center">
            <CCol md sm="12" className="mb-sm-2 mb-0">
              <div className="text-muted">Well</div>
              <strong>1500</strong>
              <CProgress
                className="progress-xs mt-2"
                precision={1}
                color="info"
                value={90}
              />
            </CCol>
          </CRow>
        </CCardFooter>
      </CCard>
    </>
  );
};

export default Dashboard;
