import React from "react";
import moment from "moment";
import "react-dates/initialize";
import { DateRangePicker } from "react-dates";
import "react-dates/lib/css/_datepicker.css";
import { CCard, CCardHeader, CCardBody, CBadge } from "@coreui/react";

const DatePicker = () => {
  const [date, setDate] = React.useState({ startDate: null, endDate: null });
  const [focused, setFocused] = React.useState();
  const [num, setNum] = React.useState({
    deviceType: "WELL",
    measurementType: "NORMAL",
    searchDateEnd: {
      day: "",
      month: "",
      year: "",
    },
    searchDateStart: {
      day: "",
      month: "",
      year: "",
    },
  });
  const aa = moment(date.startDate).format("YYYY/M/DD");

  React.useEffect(() => {
    setNum((prev) => ({
      ...prev,
      searchDateEnd: {
        // day: moment(date.endDate).format("DD"),
        // month: moment(date.endDate).format("M"),
        // year: moment(date.endDate).format("YYYY"),
      },
      searchDateStart: {
        // day: moment(date.startDate).format("DD"),
        // month: moment(date.startDate).format("M"),
        year: aa.split("/")[2],
      },
    }));
  }, []);
  const obj = {
    year: aa.split(),
  };
  // console.log(Object.keys(num).join(""));
  console.log(aa.split("/"));
  // const v = date.startDate;
  // console.log(moment());
  console.log("num =>", obj);
  return (
    <DateRangePicker
      startDate={date.startDate}
      startDateId="startDate"
      endDate={date.endDate}
      endDateId="endDate"
      onDatesChange={(value) => setDate(value)}
      focusedInput={focused}
      onFocusChange={(focusedInput) => setFocused(focusedInput)}
      orientation="horizontal"
      openDirection="down"
    />
  );
};

export default DatePicker;
